    update_time = #{updateTime}
where id = #{id}

mapper.select:
SELECT exists(
    SELECT 1
    FROM access_ctrl_indu_modbus
    WHERE device_id = #{deviceId}
      AND function_code = #{functionCode}
      AND rw = #{rw}
      AND status = '0'  -- 假设状态为'0'表示正常
    LIMIT 1
)
